@use "variables" as *;

header {
  background: url(../images/mobile/image-hero.jpg) no-repeat;
  background-size: cover;
  color: $clr-white;
  font-family: $ff-heading;
  padding: 2.5rem 1.5rem;

  @media (min-width: 760px) {
    background: url(../images/desktop/image-hero.jpg) no-repeat;
    background-size: cover;
    padding: 3rem 5rem;
  }

  @media (min-width: 1100px) {
    padding: 3.5rem 10rem;
  }
}

nav {
  display: flex;
  align-items: center;
  justify-content: space-between;
}

nav.active {
  background: $clr-black;
}

// LOGO

.logo {
  cursor: pointer;
  width: 9rem;
  opacity: 1;
  transition: all .2s ease-in-out;
  position: relative;
  z-index: 3;

  @media (min-width: 560px) {
    width: 11rem;
  }

  @media (min-width: 860px) {
    width: 12.5rem;
  }
}

.logo:hover {
  opacity: .85;
}

// HAMBURGER

.hamburger {
  all: unset;
  background-color: transparent;
  cursor: pointer;

  span {
    background-color: $clr-white;
    display: block;
    height: 2px;
    width: 25px;
    margin-bottom: 6px;
    transform-origin: 6.6px 1px;
    transition: all .4s ease-out;
  }

  @media (min-width: 860px) {
    display: none;
  }
}

.hamburger.active span:nth-child(1) {
	transform: rotate(45deg);
}

.hamburger.active span:nth-child(2) {
	display: none;
}

.hamburger.active span:nth-child(3) {
	transform: rotate(-45deg);
}

.nav-menu {
  list-style: none;
  display: none;
  text-transform: uppercase;

  a,
  a:active {
    color: $clr-white;
    text-decoration: none;
  }

  @media (min-width: 860px) {
    display: flex;
    gap: 2rem;
    font-family: $ff-main;
    text-transform: initial;
  }
}

.nav-menu.active {
  background: $clr-black;
  display: flex;
  flex-direction: column;
  gap: 2rem;
  position: absolute;
  top: 0;
  left: 0;
  width: 100vw;
  height: 100vh;
  font-size: 1.5rem;
  justify-content: center;
  padding-left: 1.5rem;
}

.header-title {
  border: 2px solid $clr-white;
  font-size: 1.2rem;
  margin: 11rem auto;
  padding: 1.5rem;
  line-height: 1;
  text-transform: uppercase;
  max-width: 25rem;

  @media (min-width: 860px) {
    font-size: 2rem;
    max-width: 38rem;
    padding: 2.5rem;
    margin: 11rem 0;
  }
}

